import React, { useState, useEffect, useRef } from 'react';
import { FaHeart, FaComment, FaEllipsisV } from 'react-icons/fa';
import axios from 'axios';
import './Card.css';

const Card = ({ post, currentUser, onDelete }) => {
  const [likes, setLikes] = useState([]);
  const [liked, setLiked] = useState(false);
  const [comments, setComments] = useState(post.comments || []);
  const [commentText, setCommentText] = useState('');
  const [menuOpen, setMenuOpen] = useState(false);
  const inputRef = useRef(null);

  // Fetch likes for this post
  useEffect(() => {
    async function fetchLikes() {
      try {
        const res = await axios.get(`http://localhost:5000/likes/${post._id}`);
        setLikes(res.data.likes);
      } catch (err) {
        console.error('Failed to fetch likes', err);
      }
    }
    fetchLikes();
  }, [post._id]);

  // Check if current user liked
  useEffect(() => {
    setLiked(likes.some(l => l.userid === currentUser._id));
  }, [likes, currentUser._id]);

  // Toggle like/unlike
  const handleLike = async () => {
    try {
      if (!liked) {
        const res = await axios.post(`http://localhost:5000/likes/${post._id}`, {
          userid: currentUser._id,
        });
        setLikes(res.data.likes);
        setLiked(true);
      } else {
        const res = await axios.delete(`http://localhost:5000/likes/${post._id}`, {
          data: { userid: currentUser._id },
        });
        setLikes(res.data.likes);
        setLiked(false);
      }
    } catch (err) {
      console.error('Like failed:', err);
    }
  };

  const handleAddComment = () => {
    if (commentText.trim() === '') return;
    setComments(prev => [...prev, commentText]);
    setCommentText('');
  };

  const handleCommentClick = () => {
    inputRef.current?.focus();
  };

  const handleDelete = () => {
    if (window.confirm("Are you sure you want to delete this post?")) {
      onDelete?.(post._id);
    }
    setMenuOpen(false);
  };

  return (
    <div className="card">
      <div className="card-header d-flex align-items-center justify-content-between">
        <div className="d-flex align-items-center gap-2">
          <img
            src="https://static.vecteezy.com/system/resources/thumbnails/007/296/447/small_2x/user-icon-in-flat-style-person-icon-client-symbol-vector.jpg"
            alt="User"
            className="user-avatar"
          />
          <strong>{post.user.name || post.user}</strong>
        </div>
        <div className="position-relative">
          <FaEllipsisV
            style={{ cursor: 'pointer' }}
            onClick={() => setMenuOpen(!menuOpen)}
          />
          {menuOpen && (
            <div className="menu-dropdown">
              <button className="dropdown-item" onClick={handleDelete}>
                Delete
              </button>
            </div>
          )}
        </div>
      </div>

      <div className="card-body">
        {post.uploadtype === "video" && (
          <iframe
            width="100%"
            height="380px"
            src={post.upload}
            frameBorder="0"
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share"
            referrerPolicy="strict-origin-when-cross-origin"
            allowFullScreen
            title="video"
          ></iframe>
        )}
        {post.uploadtype === "image" && (
          <img
            src={post.upload}
            className="post-media"
            alt="Uploaded content"
            width="100%"
          />
        )}
        <p className="card-text">{post.content}</p>

        <div className="post-actions my-2 d-flex gap-3">
          <button
            className="btn btn-light d-flex align-items-center gap-1"
            onClick={handleLike}
            style={{ color: liked ? 'red' : 'black' }}
          >
            <FaHeart /> {likes.length}
          </button>
          <button
            className="btn btn-light d-flex align-items-center gap-1"
            onClick={handleCommentClick}
            style={{ color: 'black', marginLeft: '40px' }}
          >
            <FaComment /> {comments.length}
          </button>
        </div>

        <div className="d-flex gap-2 mb-2">
          <input
            ref={inputRef}
            type="text"
            className="form-control"
            placeholder="Add a comment..."
            value={commentText}
            onChange={(e) => setCommentText(e.target.value)}
          />
          <button className="btn btn-primary" onClick={handleAddComment}>
            Send
          </button>
        </div>

        <div className="comments-list">
          {comments.map((c, i) => (
            <div key={i} className="border-top pt-1">
              {c}
            </div>
          ))}
        </div>

        <span className="post-time" style={{ fontSize: '0.9rem', color: 'gray' }}>
          {new Date(post.time).toLocaleString()}
        </span>

        {likes.length > 0 && (
          <div style={{ fontSize: '0.85rem', color: 'gray', marginTop: '4px' }}>
            Liked by: {likes.map(l => l.username).join(', ')}
          </div>
        )}
      </div>
    </div>
  );
};

export default Card;
