                        {post.map((val) => (
                            <tr >
                                <td>  {val.userid}</td>
                                <td>
                                    {val.video !== null && val.video !="" ? <iframe width="200px" height="200px" src={val.video} frameBorder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share" referrerpolicy="strict-origin-when-cross-origin" allowfullscreen
                                    ></iframe> : ""}
                                    {val.image !== null && val.image !="" ? <img src={val.image} height="100px" width="200px" /> : ""}
                                </td>
                                <td>{val.content}</td>
                                <td>
                                    <button className="btn btn-danger mx-3" onClick={() => handleDelete(val.postid)}>Delete</button>
                                    <button className="btn btn-primary" onClick={() => handleUpdate(val.postid)}>Update</button>
                                </td>

                            </tr>
                        ))}

                    </tbody>
                </table>
            </div>
        </>
    )
}
