import axios from 'axios';
import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
export default function List() {
    let [college, setcollege] = useState([]);
    let route = useNavigate()

    async function getcollege() {
        let res = await axios.get('https://chat-with-friends-mocha.vercel.app/users')
        setcollege(res.data)
        console.log(res.data)
    }
    useEffect(() => {

        let user = localStorage.getItem("user")
        if(user){
            getcollege()
        }
    }, [])

    async function handleDelete(id) {
        let res = await axios.delete(`https://chat-with-friends-mocha.vercel.app/users/${id}`)
      console.log(res.data)
        getcollege();

    }
    function handleUpdate(id) {
        localStorage.setItem("id", id)
        route("/update")
    }

    return (
        <>
            <div className='container mt-5 table-responsive '>
                <h2>Users List</h2>
                <table className=' table table-striped mt-3'>
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Phone</th>
                            <th>Email</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {college.map((val) =>
                            <tr key={val._id}>
                                <td> <p>{val.name}</p></td>
                                <td><p>{val.phone}</p></td>
                                <td><p>{val.email}</p></td>
                                <td>
                                    <button className='btn btn-danger ' onClick={() => handleDelete(val._id)}><i class="fa-solid fa-trash"></i></button>
                                    <button className='btn btn-primary' onClick={() => handleUpdate(val._id)}>
                                   <i class="fa-solid fa-pen"></i></button>
                                </td>

                            </tr>
                        )}
                    </tbody>
                </table>
            </div>
        </>
    )

}
