        console.log(res.data);
        route("/table");
    }

    useEffect(() => {
        let postid = localStorage.getItem("postid")
        async function getpost() {
            const res = await axios.get(`http://localhost:3005/post/${id}`);
            console.log(res.data);
            seteditpost(res.data[0]);
        } getpost()

    }, []);


    return (
        <>

            <div className="container mt-5">
                <h2>Edit a Post.</h2>
                <form onSubmit={handleSubmit}>
                    <div className="mt-3">
                        <label htmlFor="userid" className="form-label">Userid</label>
                        <input type="text" className="form-control" id="userid" onChange={handleChange} value={editpost.userid && editpost.userid} />

                    </div>

                    <div className="mt-3">
                       <input type="radio" id="uploadtype" className="mx-2" name="uploadtype" value="image" onChange={handleChange}  />
                        <label htmlFor="image">IMAGE</label>

                        <input type="radio" className="mx-2" id="uploadtype" name="uploadtype" value="video" onChange={handleChange}   />
                        <label htmlFor="video">VIDEO</label>                        
                        <input type="text" className="form-control" id="upload" onChange={handleChange} value={editpost.upload && editpost.upload} />
                    </div>

                    <div className="mt-3">
                        <label htmlFor="content" className="form-label"> Content</label>
                        <input type="text" className="form-control" id="content" placeholder="Write here..." onChange={handleChange} value={editpost.content && editpost.content} />
                    </div>
                    <button className="btn btn-primary mt-3">Post</button>
                </form>
            </div>
        </>
    )
}
