import React, { useState, useRef } from 'react';
import { FaHeart, FaComment, FaEllipsisV } from 'react-icons/fa';
import './Card.css';

const Card = ({ post, onDelete }) => {
  const [likes, setLikes] = useState(post.likes || 0);
  const [comments, setComments] = useState(post.comments || []);
  const [commentText, setCommentText] = useState('');
  const [menuOpen, setMenuOpen] = useState(false);
  const inputRef = useRef(null);

   const handleLike = () => {
    let res=
      setLikes(prev => prev + 1);
    }

  const handleAddComment = () => {
    if (commentText.trim() === '') return;
    setComments(prev => [...prev, commentText]);
    setCommentText('');
  };

  const handleCommentClick = () => {
    inputRef.current?.focus();
  };

  return (
    <div className="card">
      {/* Header: User + Three-dot menu */}
      <div className="card-header d-flex align-items-center justify-content-between">
        <div className="d-flex align-items-center gap-2">
          <img
            src="https://static.vecteezy.com/system/resources/thumbnails/007/296/447/small_2x/user-icon-in-flat-style-person-icon-client-symbol-vector.jpg"
            alt="User"
            className="user-avatar"
          />
          <strong>{post.user.name || post.user}</strong>
        </div>

        {/* Three-dot menu */}
        <div className="position-relative">
          <FaEllipsisV
            style={{ cursor: 'pointer' }}
            onClick={() => setMenuOpen(!menuOpen)}
          />

        </div>
      </div>

      {/* Post Content */}
      <div className="card-body">
        {post.uploadtype === "video" && (
          <iframe
            width="100%"
            height="380px"
            src={post.upload}
            frameBorder="0"
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share"
            referrerPolicy="strict-origin-when-cross-origin"
            allowFullScreen
            title="video"
          ></iframe>
        )}
        {post.uploadtype === "image" && (
          <img src={post.upload} className="post-media" alt="Uploaded content" width="100%" height="150px"/>
        )}
        <p className="card-text">{post.content}</p>

        {/* Like & Comment Buttons */}
        <div className="post-actions my-2 d-flex gap-3">
          <button
            className="btn btn-light d-flex align-items-center gap-1"
            onClick={handleLike}
            style={{ color: 'black' }}
          >
            <FaHeart /> {likes}
          </button>
          <button
            className="btn btn-light d-flex align-items-center gap-1"
            onClick={handleCommentClick}
            style={{ color: 'black', marginLeft: '40px' }}
          >
            <FaComment /> {comments.length}
          </button>
        </div>

        {/* Comment Input */}
        <div className="d-flex gap-2 mb-2">
          <input
            ref={inputRef}
            type="text"
            className="form-control"
            placeholder="Add a comment..."
            value={commentText} 
            onChange={(e) => setCommentText(e.target.value)}
          />
          <button className="btn btn-primary" onClick={handleAddComment}>
            Send
          </button>
        </div>

        {/* Display Comments */}

        <div className="comments-list">

          {comments.map((c, i) => (
            <div key={i} className="border-top pt-1 ">
           {post.user}<br/>
             {c}<br/> 
            </div>
          ))}
        </div>

        {/* Post Time */}
        <span className="post-time" style={{ fontSize: '0.9rem', color: 'gray' }}>
          {new Date(post.time).toLocaleString()}
        </span>
      </div>
    </div>
  );
};

export default Card;
