import { useState } from "react"
import { useNavigate } from "react-router-dom";
import axios from ''

export default function Create() {
let [data,setdata]=useState([]);
let route=useNavigate();

 function handleChange(e) {
    setdata({...data,[e.target.id]:e.target.value})
}

 async function handleSubmit(e){
    e.preventDefault();
    console.log(data);
    let res=await axios.post("http://localhost:3002",data)
    console.log(res.data)
    route("/list")

}

    return(
<>
        <div className="container mt-5">
            <h2>Students Information</h2>
            <form onSubmit={handleSubmit}>
                <div className="mt-3">
                    <label htmlFor="name" className="form-label" >Name</label>
                    <input type="text" className="from-control" id="name" placeholder="Enter your Name" onChange={handleChange} value={data.name} />
                </div>
                <div className="mt-3">
                    <label htmlFor="phone" className="form-label" >Phone</label>
                    <input type="phone" className="from-control" id="name" placeholder="Enter your Phone" onChange={handleChange} value={data.phone} />
                </div>
                <div className="mt-3">
                    <label htmlFor="email" className="form-label" >Email</label>
                    <input type="email" className="from-control" id="email" placeholder="Enter your Email" onChange={handleChange} value={data.email} />
                </div>
                <div className="mt-3">
                    <label htmlFor="passwrd" className="form-label" >Password</label>
                    <input type="password" className="from-control" id="password" placeholder="Enter your password" onChange={handleChange} value={data.password} />
                </div>
                <button className="btn btn-primary">Submit</button>

            </form>
        </div>
    </>
    )

}
