                        <input type="password" className="form-control" id="password" placeholder="Enter your Password" onChange={handleChange} value={edit.password && edit.password} />
                    </div> */}
                    <button className="btn btn-primary mt-3">Submit</button>

                </form>
            </div>
        </>
    )

}
