import axios from "axios";
import { useEffect, useState } from "react"
import { useNavigate } from "react-router-dom";

export default function Show() {
    let [users, setusers] = useState([]);


    const route = useNavigate();

    async function getusers() {
        let res = await axios.get("http://localhost:3005/post");
        console.log(res.data)
        setusers(res.data)

    }

    async function handleDelete(postid) {
        let res = await axios.delete(`http://localhost:3005/post/${postid}`)
        console.log(res.data)
        getusers();

    }

    useEffect(() => {
        getusers();
    }, []);

    async function handleUpdate(id) {
        localStorage.setItem("id", id)
        route("/edit")

    }

    return (
        <>
            <div className=" container mb-4">
                <h2 className=" container ">Posts Table</h2>


                <table className="table table-striped responsive">
                    <thead>
                        <tr>
                            <th> Userid</th>
                            <th> Upload</th>
                            <th> Content</th>
                            <th> Action</th>
                        </tr>
                    </thead>
                    <tbody>

                        {users.map((val) => 
                            <tr key={val._id}>
                                <td>{val._id}</td>
                                <td>
                                    {val.uploadvideo !== null && val.video != "" ? <iframe width="200px" height="200px" src={val.video} frameBorder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share" referrerpolicy="strict-origin-when-cross-origin" allowfullscreen
                                    ></iframe> : ""}
                                    {val.image !== null && val.image != "" ? <img src={val.image} height="100px" width="200px" /> : ""}
                                </td>
                                <td>{val.content}</td>
                                <td>
                                    <button className="btn btn-danger mx-3" onClick={() => handleDelete(val._id)}><i class="fa-solid fa-trash"></i></button>
                                    <button className="btn btn-primary" onClick={() => handleUpdate(val._id)}><i class="fa-solid fa-pen"></i></button>
                                </td>

                            </tr>
                        )}

                    </tbody>
                </table>
            </div>
        </>
    )
}
