import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import './Card.css';
import apiUrl from "../../apiConfig";

export default function Show() {
    let [posts, setPosts] = useState([]);
    const route = useNavigate();

    async function getPosts() {
        try {
            let res = await axios.get(`${apiUrl}/posts`);
            setPosts(res.data);
        } catch (error) {
            console.error("Error fetching posts:", error);
        }
    }

    async function handleDelete(postid) {
        try {
            await axios.delete(`${apiUrl}/posts/${postid}`);
            getPosts();
        } catch (error) {
            console.error("Error deleting post:", error);
        }
    }

    useEffect(() => {
        getPosts();
    }, []);

    function handleUpdate(id) {
        localStorage.setItem("id", id);
        route("/edit");
    }

    return (
        <div className="container mt-5">
            <h2 className="text-center mb-4">Posts</h2>
            <div className="row">
                {posts.map((post) => (
                    <div className="col-md-6 offset-md-3" key={post._id}>
                        <div className="card">
                            <div className="card-header">
                                <img src="https://via.placeholder.com/40" alt="User" />
                                <strong>{post.userid.name}</strong>
                            </div>
                            <div className="card-body">
                                <p className="card-text">{post.content}</p>
                                {post.uploadtype === "video" ? (
                                    <iframe width="100%" height="315" src={post.upload} frameBorder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share" referrerPolicy="strict-origin-when-cross-origin" allowFullScreen></iframe>
                                ) : post.uploadtype === "image" ? (
                                    <img src={post.upload} className="post-media" alt="Uploaded content" />
                                ) : null}
                            </div>
                            <div className="card-footer text-muted">
                                <button className="btn btn-danger mx-2" onClick={() => handleDelete(post._id)}>
                                    <i className="fa-solid fa-trash"></i> Delete
                                </button>
                                <button className="btn btn-primary" onClick={() => handleUpdate(post._id)}>
                                    <i className="fa-solid fa-pen"></i> Edit
                                </button>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}
