import React, { useState, useRef } from 'react';
import { FaHeart, FaComment, FaEllipsisV } from 'react-icons/fa';
import './Card.css';
import axios from 'axios';
import apiUrl from "../../apiConfig";

const Card = ({ post ,onDelete}) => {
  const [likes, setlikes] = useState(post.likes || 0);
  const [liked, setLiked] = useState(post.isLiked || false);
  const [comments, setComments] = useState(post.comments || []);
  const [commentText, setCommentText] = useState('');
  const [menuOpen, setMenuOpen] = useState(false);
  const inputRef = useRef(null);

  async function handleLike() {
  const user = JSON.parse(localStorage.getItem('user'));
  try {
    // ✅ Fix: remove ":" in URL
    const res = await axios.post(`${apiUrl}/like/${post._id}`, { user });
     console.log(res.data)
    // ✅ Toggle like state
    setLiked((prev) => !prev);

    // ✅ Update like count
    setlikes((prev) => (liked ? prev - 1 : prev + 1));

    // ✅ If you want to fetch updated like count from backend:
    const countRes = await axios.get(`${apiUrl}/like/${post._id}`,);
    console.log("Updated count:", countRes.data);

    console.log(res.data);
  } catch (err) {
    console.error("Error liking post:", err.response?.data || err.message);
  }
}


  const handleAddComment = () => {
    if (commentText.trim() === '') return;
    setComments(prev => [...prev, commentText]);
    setCommentText('');
  };

  const handleCommentClick = () => {
    inputRef.current?.focus();
  };

  const handleDelete = () => {
    if (window.confirm("Are you sure you want to delete this post?")) {
      onDelete?.(post._id); // Call parent delete function
    }
    setMenuOpen(false);
  };

  return (
    <div className="card">
      {/* Header: User + Three-dot menu */}
      <div className="card-header d-flex align-items-center justify-content-between">
        <div className="d-flex align-items-center gap-2">
          <img
            src="https://static.vecteezy.com/system/resources/thumbnails/007/296/447/small_2x/user-icon-in-flat-style-person-icon-client-symbol-vector.jpg"
            alt="User"
            className="user-avatar"
          />
          <strong>{post.user.name || post.user}</strong>
        </div>

        {/* Three-dot menu */}
        <div className="position-relative">
          <FaEllipsisV
            style={{ cursor: 'pointer' }}
            onClick={() => setMenuOpen(!menuOpen)}
          />
          {menuOpen && (
            <div className="menu-dropdown">
              <button className="dropdown-item " onClick={handleDelete}>
                Delete
              </button>
            </div>
          )}
        </div>
      </div>

      {/* Post Content */}
      <div className="card-body">
        {post.uploadtype === "video" && (
          <iframe
            width="100%"
            height="380px"
            src={post.upload}
            frameBorder="0"
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share"
            referrerPolicy="strict-origin-when-cross-origin"
            allowFullScreen
            title="video"
          ></iframe>
        )}
        {post.uploadtype === "image" && (
          <img src={post.upload} className="post-media mb-3" alt="Uploaded content" width="100%" height="150px" />
        )}
        <p className="card-text">{post.content}</p>

        {/* Like & Comment Buttons */}
        <div className="post-actions my-2 d-flex gap-3">
          <button
            className="btn btn-light d-flex align-items-center gap-1"
            onClick={handleLike}
            style={{ color: liked ? "red" : 'black' }}
          >
            <FaHeart /> {likes}
          </button>
          <button
            className="btn btn-light d-flex align-items-center gap-1"
            onClick={handleCommentClick}
            style={{ color: 'black', marginLeft: '40px' }}
          >
            <FaComment /> {comments.length}
          </button>
        </div>

        {/* Comment Input */}
        <div className="d-flex gap-2 mb-2">
          <input
            ref={inputRef}
            type="text"
            className="form-control"
            placeholder="Add a comment..."
            value={commentText}
            onChange={(e) => setCommentText(e.target.value)}
          />
          <button className="btn btn-primary" onClick={handleAddComment}>
            Send
          </button>
        </div>

        {/* Display Comments */}

        <div className="comments-list">

          {comments.map((c, i) => (
            <div key={i} className="border-top pt-1 ">
              {post.user}<br />
              {c}<br />
            </div>
          ))}
        </div>

        {/* Post Time */}
        <span className="post-time" style={{ fontSize: '0.9rem', color: 'gray' }}>
          {new Date(post.time).toLocaleString()}
        </span>
      </div>
    </div>
  );
};

export default Card;
