import axios from 'axios';
import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function List() {
  let [college, setCollege] = useState([]);
  let route = useNavigate();

  async function getCollege() {
    let res = await axios.get('https://chat-with-friends-mocha.vercel.app/users');
    setCollege(res.data);
  }

  useEffect(() => {
    let user = localStorage.getItem("user");
    if (user) {
      getCollege();
    }
  }, []);

  async function handleDelete(id) {
    await axios.delete(`https://chat-with-friends-mocha.vercel.app/users/${id}`);
    getCollege();
  }

  function handleUpdate(id) {
    localStorage.setItem("id", id);
    route("/update");
  }

  return (
    <div className="container mt-5">
      <h2 className="mb-3 text-center">Users List</h2>

      {/* Responsive wrapper */}
      <div className="table-responsive">
        <table className="table table-striped table-hover align-middle">
          <thead className="table-dark">
            <tr>
              <th scope="col">Name</th>
              <th scope="col">Phone</th>
              <th scope="col">Email</th>
              <th scope="col" className="text-center">Action</th>
            </tr>
          </thead>
          <tbody>
            {college.map((val) => (
              <tr key={val._id}>
                <td>{val.name}</td>
                <td>{val.phone}</td>
                <td>{val.email}</td>
                <td className="text-center">
                  <button
                    className="btn btn-sm btn-danger me-2"
                    onClick={() => handleDelete(val._id)}
                  >
                    <i className="fa-solid fa-trash"></i>
                  </button>
                  <button
                    className="btn btn-sm btn-primary"
                    onClick={() => handleUpdate(val._id)}
                  >
                    <i className="fa-solid fa-pen"></i>
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
